import styled from 'styled-components';

export const StyledHeader = styled.header`
    background-color: ${(props) => props.theme.colors.header};
    padding: 40px 20px;

    @media (max-width:${({theme}) => theme.widths.mobile}) {
        background-color: '#1234';
    }
`

