/* eslint-disable array-callback-return */
/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable @typescript-eslint/no-unused-expressions */
import React, { FC, useEffect, useState } from 'react'
// import { useState } from 'react'
// import { getUsers } from '../../api'
import { useAppDispatch, useAppSelector, useUsersDataFromApi } from '../../state/hooks'
import User from '../../models/User'

interface Props {
    searchName:string;
}

const UsersList: FC<Props> = ({searchName}) => {
    const users = useAppSelector((state => state.users.data))

    useUsersDataFromApi()

    const queriedUsers: User[] = users.filter((user: User)=> {
        if (user.name.toLowerCase().indexOf(searchName.toLowerCase()) > -1) return true;
        return false
    })

    const getUsersListItemElements = (users: User[]): JSX.Element[] => {
        return users.map(user => {
            return <li key={user.id}>{user.name}</li>
        })
    }
    
    return (
        <ol>
            {getUsersListItemElements(
                queriedUsers.length === 0 && !searchName 
                ? users 
                : queriedUsers)}
            
        </ol>
    )
}

export default UsersList
