import styled from 'styled-components';

export const StyledHeader = styled.header`
    background-color: ${(props) => props.theme.colors.header};
    padding: 40px 20px;
`

export const Nav = styled.nav`
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 40px;
`
