/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable @typescript-eslint/no-unused-expressions */
import React, { FC, useEffect, useState } from 'react'
// import { useState } from 'react'
// import { getUsers } from '../../api'
import { useAppDispatch, useAppSelector, useUsersDataFromApi } from '../../state/hooks'

interface Props {
    searchName:string;
}

const UsersList: FC<Props> = ({searchName}) => {
    const users = useAppSelector((state => state.users.data))
    // console.log('UsersList')
    // const [clicked, setClicked] = useState<boolean>(false)
    useUsersDataFromApi()
    const queriedUsers = users.map((user) => {
        if (user.name.indexOf(searchName) > -1) return true
        return false
    })

    const getUsersListItems = (users) => {
        return users.map(user => {
            return <li key={user.id}>{user.name}</li>
        })
    }
    
    return (
        <ul>
            {getUsersListItems(queriedUsers.length === 0 ? users : queriedUsers)}
            {/* <button onClick={() => {
                setClicked(!clicked)
                console.log("Clicked")
            }}>Test</button> */}
        </ul>
    )
}

export default UsersList
